

// Avoid `console` errors in browsers that lack a console.
(function() {
    var method;
    var noop = function () {};
    var methods = [
        'assert', 'clear', 'count', 'debug', 'dir', 'dirxml', 'error',
        'exception', 'group', 'groupCollapsed', 'groupEnd', 'info', 'log',
        'markTimeline', 'profile', 'profileEnd', 'table', 'time', 'timeEnd',
        'timeStamp', 'trace', 'warn'
    ];
    var length = methods.length;
    var console = (window.console = window.console || {});

    while (length--) {
        method = methods[length];

        // Only stub undefined methods.
        if (!console[method]) {
            console[method] = noop;
        }
    }
}());

// Place any jQuery/helper plugins in here.

(function( $ ){
    $.fn.slider = function(options) {
        var $this = this;
        var settings = $.extend({
            easing: "easeInQuart",
            easingDuration: 700,
            slideDuration: 5000,
            clickTimeout: 5000,
            slidesContainer: $(".slider-container"),
            slidesControlPrev: $(".slider-control-prev"),
            slidesControlNext: $(".slider-control-next"),
            slidesThumbs: $(".slider-thumb"),
            slidesActiveThumbClass: "slider-thumb-active",
            autoPlay: true,
            imageClick : false,
            thumbClick : true,
            enableKeyboard: false,
            useSwipes: true
        }, options );
    
        var $container = $this.find(settings.slidesContainer);
        var $controlPrev = $this.find(settings.slidesControlPrev);
        var $controlNext = $this.find(settings.slidesControlNext);
        var $thumbs = $this.find(settings.slidesThumbs); 
        
        if($container.find("li").length > 1) {
        
            function prevSlide(amount) {
                amount = typeof amount == "undefined" ? 1 : amount;
                for(var i=0; i<amount; i++)
                    $container.find("li").last().detach().insertBefore($container.find("li").first());
                $container.css({
                    left: -$container.find("li").first().width()*amount
                }); 
                $container.animate({
                        left: 0
                    },
                    settings.easingDuration,
                    settings.easing,
                    function(){
                        var currentThumbIndex = $thumbs.index($thumbs.filter("." + settings.slidesActiveThumbClass));
                        var newIndex = currentThumbIndex - amount < 0 ? $thumbs.length - 1 : currentThumbIndex - amount;
                        $thumbs.removeClass(settings.slidesActiveThumbClass).eq(newIndex).addClass(settings.slidesActiveThumbClass);
                    }
                );
            }
            
            function nextSlide(amount) {
                amount = typeof amount == "undefined" ? 1 : amount;
                $container.animate({
                       left: -$container.find("li").first().width()*amount
                    },
                    settings.easingDuration,
                    settings.easing,
                    function(){
                        for(var i=0; i<amount; i++)
                            $container.find("li").first().detach().insertAfter($container.find("li").last());
                        $container.css({
                            left: 0
                        });
                        var currentThumbIndex = $thumbs.index($thumbs.filter("." + settings.slidesActiveThumbClass));
                        var newIndex = currentThumbIndex + amount >= $thumbs.length ? 0 : currentThumbIndex + amount;
                        $thumbs.removeClass(settings.slidesActiveThumbClass).eq(newIndex).addClass(settings.slidesActiveThumbClass);
                    }
                );    
            }
            
            if(settings.useSwipes) {
                $container.swipe({
                    threshold: 50,
                    allowPageScroll: 'vertical',
                    triggerOnTouchEnd: false,
                    fallbackToMouseEvents: false,
                    swipeLeft: function(){
                        pauseAutoPlay();
                        nextSlide();
                        resumeAutoPlay();                        
                    },
                    swipeRight: function(){
                        pauseAutoPlay();
                        prevSlide();
                        resumeAutoPlay();                        
                    } 
                });
            }
            
            function autoPlay()
            {
                $this.data("interval", setInterval(function(){nextSlide();}, settings.slideDuration));
            }
    
            function pauseAutoPlay()
            {
                if(settings.autoPlay)
                    clearInterval($this.data("interval"));
            }
            
            function resumeAutoPlay()
            {
                if(settings.autoPlay)
                    autoPlay();
            }
            
            if(settings.thumbClick) {
                $thumbs.click(function(){
                    pauseAutoPlay();
                    var $this = $(this);
                    var clickedIndex = $thumbs.index($this);
                    var currentIndex = $thumbs.index($thumbs.filter("." + settings.slidesActiveThumbClass));
                    var amount = clickedIndex - currentIndex;
                    if(amount > 0)
                        nextSlide(amount);
                    else if(amount < 0)
                        prevSlide(amount * -1);
                    resumeAutoPlay();
                    return false;
                });
            }
                        
            $controlNext.click(function(){
                pauseAutoPlay();
                nextSlide();
                resumeAutoPlay();
                return false;    
            });
        
            $controlPrev.click(function(){
                pauseAutoPlay();
                prevSlide();
                resumeAutoPlay();
                return false;    
            });
            
            if(settings.enableKeyboard) {
                $(document).keyup(function (e) {
                    if(e.keyCode == 37) 
                        $controlPrev.click();
                    else if(e.keyCode == 39)
                        $controlNext.click();
                });
            }
            
            if(settings.imageClick) {
                $container.find("img").css({cursor: "pointer"}).click(function(){
                    $controlNext.click();
                });
            }
            
                
            if(settings.autoPlay)
            {
                autoPlay();
            }
        
        }
    
        return this;
    };
})( jQuery );



