( function( $ ) {

    oxFlyOutBox = {

        _create: function(){

            var self = this,
                options = self.options,
                el      = self.element;



            $(document).click( function( e ){
                if( $(e.target).parents("div").hasClass("topPopList") ){
                }else{
                    $("div.flyoutBox").hide();
                }
            });

            $(document).keydown( function( e ) {
               if( e.which == 27) {
                    $("div.flyoutBox").hide();
               }
            });

            el.mouseenter(function(){
                $("div.flyoutBox").hide();
                $(this).nextAll("div.flyoutBox").show();
                
                $(this).mouseleave(function(){
					$(this).find(".flyoutBox").hide();
                });
                return false;
            });

			$(".topPopList").on("mouseleave", function() {
				if ($(this).find(".flyoutbox:visible").length > 0) {
					$(this).find(".flyoutbox").hide();
				}
			});

			$(".topPopList").on("mouseleave", function() {
				$(".flyoutBox").hide();
			});


        }
    }

    $.widget( "ui.oxFlyOutBox", oxFlyOutBox );

} )( jQuery );


( function( $ ) {

	var ddTopNav = {

		options: {
			sensitivity: 6,		// 1 = mouse must not move between polling intervals, 10 = higher risk of false positives, default = 6
			interval: 100,		// polling interval in milliseconds, default = 100
			timeout: 10			// number = milliseconds delay before onMouseOut, default = 0
		},

		_create: function() {

			var self = this,
				options = self.options,
				el      = self.element;


			var topnav_items  = $('#top_nav').children('ul').children('li').filter(function(index) {
				if ($('div.sub', this).length > 0 == "undefined") return false;
				return $('div.sub', this).length > 0;
			});


			function justOpen() {
				var that = $(this);
				that.addClass('open');
				that
					.children('div')
					.show()
				;
			};
			
			function justClose() {
				var that = $(this);
				that
					.removeClass('open')
					.children('div')
					.hide()
				;
			};

			topnav_items.mouseenter(function(e){
				e.preventDefault();
			});

			topnav_items.hoverIntent({
					over: justOpen,
					out: justClose,
					sensitivity: self.options['sensitivity'],
					interval: self.options['interval'],
					timeout: self.options['timeout']
			});


		}
	}

	$.widget( 'ui.ddTopNav', ddTopNav );

} )( jQuery );


